<!--集团信息，第四级包裹--><template >  <div class="rightInfo">    <div class="topTitle">      <h4>员工列表</h4>    </div>    <div class="centerContent">      <div class="item user">        <el-row>          <el-input            placeholder="筛选员工"            v-model="filterText">          </el-input>          <el-button type="success" @click="showAddNew=true">添加新员工</el-button>          <el-button type="primary"@click="showSetRole=true">设置角色</el-button>          <el-button type="warning" @click="openCertificate">生成企业微信凭证</el-button>        </el-row>      </div>      <el-table :data="gridData" border stripe @selection-change="handleSelectionChange" >        <el-table-column          type="selection"          width="55" >        </el-table-column >        <el-table-column property="count" label="账号" ></el-table-column>        <el-table-column property="phone" label="手机号" ></el-table-column>        <el-table-column property="depart" label="部门" ></el-table-column>        <el-table-column property="duty" label="职务" ></el-table-column>        <el-table-column property="certificateQY" label="企业微信凭证" ></el-table-column>        <el-table-column property="role" label="角色" ></el-table-column>        <el-table-column  label="操作">          <template slot-scope="scope">            <el-button @click="handleClick(scope.row)" type="text" size="small">查看</el-button>            <el-button type="text" size="small">编辑</el-button>          </template>        </el-table-column>      </el-table>    </div>    <el-dialog      title="新增用户"      :visible.sync="showAddNew"      width="40%"      >      <div class="addNewUser">        <span class="left">组织名称</span>        <span>酒店A</span>      </div>      <div class="addNewUser">        <span class="left">账号</span>        <el-input          placeholder="输入账号"          v-model="filterText">        </el-input>      </div>      <div  class="addNewUser">        <span class="left">姓名</span>        <el-input          placeholder="输入姓名"          v-model="filterText">        </el-input>      </div>      <div class="addNewUser">        <span class="left">英文名</span>        <el-input          placeholder="输入英文名"          v-model="filterText">        </el-input>      </div>      <div class="addNewUser">        <span class="left">手机号</span>        <el-input          placeholder="输入手机号"          v-model="filterText">        </el-input>      </div>      <span slot="footer" class="dialog-footer">        <el-button @click="dialogVisible = false">取 消</el-button>        <el-button type="primary" @click="dialogVisible = false">确 定</el-button>      </span>    </el-dialog>    <el-dialog      title="请选择角色"      :visible.sync="showSetRole"      width="40%"    >      <el-checkbox :indeterminate="isIndeterminate" v-model="checkAll" @change="handleCheckAllChange">全选</el-checkbox>      <div style="margin: 15px 0;"></div>      <el-checkbox-group v-model="checkedCities" @change="handleCheckedCitiesChange">        <el-checkbox v-for="city in cities" :label="city" :key="city">{{city}}</el-checkbox>      </el-checkbox-group>      </el-dialog>  </div></template><script>    const cityOptions = ['上海', '北京', '广州', '深圳'];    export default {        name: 'groupInfo',        data () {            return {                checkAll: false,                checkedCities: ['上海', '北京'],                cities: cityOptions,                isIndeterminate: true,                showSetRole:false,                showAddNew:false,                filterText:'',                gridData: [{                    count: '11111241',                    name: '王小虎',                    phone: '18723555234',                    depart:'技术部',                    duty:'前端',                    certificateQY:'是',                    role:'前端',                }, {                    count: '11111241',                    name: '王小虎',                    phone: '18723555234',                    depart:'技术部',                    duty:'前端',                    certificateQY:'是',                    role:'前端',                },                    {                        count: '11111241',                        name: '王小虎',                        phone: '18723555234',                        depart:'技术部',                        duty:'前端',                        certificateQY:'是',                        role:'前端',                    },                    {                        count: '11111241',                        name: '王小虎',                        phone: '18723555234',                        depart:'技术部',                        duty:'前端',                        certificateQY:'是',                        role:'前端',                    },                    {                        count: '11111241',                        name: '王小虎',                        phone: '18723555234',                        depart:'技术部',                        duty:'前端',                        certificateQY:'是',                        role:'前端',                    },                ],                dialogVisible:false            }        },        methods:{            openCertificate(){                this.$confirm('是否生成企业微信凭证?', '提示', {                    confirmButtonText: '确定',                    cancelButtonText: '取消',                    type: 'warning'                }).then(() => {                    this.$message({                        type: 'success',                        message: '生成成功!'                    });                }).catch(() => {                    this.$message({                        type: 'info',                        message: '已取消'                    });                });            },            handleCheckAllChange(val) {                this.checkedCities = val ? cityOptions : [];                this.isIndeterminate = false;            },            handleCheckedCitiesChange(value) {                let checkedCount = value.length;                this.checkAll = checkedCount === this.cities.length;                this.isIndeterminate = checkedCount > 0 && checkedCount < this.cities.length;            },            handleClick(){            },            handleSelectionChange(){            },            handleClose(){            }        }    }</script><!-- Add "scoped" attribute to limit CSS to this component only --><style lang="less">  .third_wrap .rightWrap .rightInfo .centerContent .item{    width: 100%;  }  .third_wrap .rightWrap .rightInfo .centerContent .item .el-input{      width:30%;    margin-right: 2rem;  }  .addNewUser{    .left{      display: inline-block;      width: 3.5rem;      height: 3rem;      margin-right: 2rem;    }    .el-input{      width: 60%;    }  }</style>