<!--集团信息，第四级包裹--><template >  <div class="rightInfo">    <div class="topTitle">      <h4>员工列表</h4>    </div>    <div class="centerContent">      <div class="item user">        <el-row>          <el-input            placeholder="筛选员工"            v-model="filterText">          </el-input>          <el-button type="success" @click="showAddNew=true">添加新员工</el-button>          <el-button type="primary">设置角色</el-button>          <el-button type="warning">生成企业微信凭证</el-button>        </el-row>      </div>      <el-table :data="gridData" border stripe @selection-change="handleSelectionChange" >        <el-table-column          type="selection"          width="55" >        </el-table-column >        <el-table-column property="count" label="账号" width="100"></el-table-column>        <el-table-column property="name" label="姓名" width="100"></el-table-column>        <el-table-column property="phone" label="手机号" width="110"></el-table-column>        <el-table-column property="depart" label="部门" width="100"></el-table-column>        <el-table-column property="duty" label="职务" width="100"></el-table-column>        <el-table-column property="certificateQY" label="企业微信凭证" width="110"></el-table-column>        <el-table-column property="role" label="角色" width="100"></el-table-column>        <el-table-column  label="操作" width="150">          <template slot-scope="scope">            <el-button @click="handleClick(scope.row)" type="text" size="small">查看</el-button>            <el-button type="text" size="small">编辑</el-button>          </template>        </el-table-column>      </el-table>    </div>    <el-dialog      title="新增用户"      :visible.sync="showAddNew"      width="40%"      >      <div class="addNewUser">        <span class="left">组织名称</span>        <span>酒店A</span>      </div>      <div class="addNewUser">        <span class="left">账号</span>        <el-input          placeholder="输入账号"          v-model="filterText">        </el-input>      </div>      <div  class="addNewUser">        <span class="left">姓名</span>        <el-input          placeholder="输入姓名"          v-model="filterText">        </el-input>      </div>      <div class="addNewUser">        <span class="left">英文名</span>        <el-input          placeholder="输入英文名"          v-model="filterText">        </el-input>      </div>      <div class="addNewUser">        <span class="left">手机号</span>        <el-input          placeholder="输入手机号"          v-model="filterText">        </el-input>      </div>      <span slot="footer" class="dialog-footer">        <el-button @click="dialogVisible = false">取 消</el-button>        <el-button type="primary" @click="dialogVisible = false">确 定</el-button>      </span>    </el-dialog>  </div></template><script>    export default {        name: 'groupInfo',        data () {            return {                showAddNew:false,                filterText:'',                gridData: [{                    count: '11111241',                    name: '王小虎',                    phone: '18723555234',                    depart:'技术部',                    duty:'前端',                    certificateQY:'是',                    role:'前端',                }, {                    count: '11111241',                    name: '王小虎',                    phone: '18723555234',                    depart:'技术部',                    duty:'前端',                    certificateQY:'是',                    role:'前端',                },                    {                        count: '11111241',                        name: '王小虎',                        phone: '18723555234',                        depart:'技术部',                        duty:'前端',                        certificateQY:'是',                        role:'前端',                    },                    {                        count: '11111241',                        name: '王小虎',                        phone: '1